class Solution:
    def numMatchingSubseq(self, s: str, words: List[str]) -> int:
        def os_iters():
            """
            Runtime: 616 ms, faster than 53.90% of Python3 online submissions for Number of Matching Subsequences.

            Approach #2: next letter pointers
            https://leetcode.com/problems/number-of-matching-subsequences/solution/

            1 <= s.length <= 5 * 104
            T: O(N)
            """
            ans = 0
            heads = [[] for _ in range(26)]
            for word in words:
                it = iter(word)
                heads[ord(next(it)) - ord("a")].append(it)

            for letter in s:
                letter_index = ord(letter) - ord("a")
                old_bucket = heads[letter_index]
                heads[letter_index] = []

                while old_bucket:
                    it = old_bucket.pop()
                    nxt = next(it, None)
                    if nxt:
                        heads[ord(nxt) - ord("a")].append(it)
                    else:
                        ans += 1
            return ans

        return os_iters()

