            def is_subseq(sub, d) -> bool:
                subd = defaultdict(list)
                last_idx = -1
                for c in sub:
                    if c not in d:
                        return False
                    if not subd[c]:
                        if last_idx >= d[c][0]:
                            return False
                        subd[c].append(d[c][0])
                    else:
                        pos = subd[c][-1]
                        idx = d[c].index(pos)
                        if idx + 1 >= len(d[c]):
                            return False
                        if last_idx >= d[c][idx + 1]:
                            return False
                        subd[c].append(d[c][idx + 1])
                return True

